function toggleTheme(){const e=document.documentElement.classList.toggle("dark");localStorage.setItem("color-theme",e?"dark":"light"),changeGiscusTheme(e?"dark":"light")}function handleThemeIcons(){const e=document.getElementById("theme-toggle-dark-icon"),t=document.getElementById("theme-toggle-light-icon");document.documentElement.classList.contains("dark")?(e.classList.add("hidden"),t.classList.remove("hidden")):(t.classList.add("hidden"),e.classList.remove("hidden"))}function handleMobileMenu(){const e=document.querySelector('[aria-controls="mobile-menu"]'),t=document.getElementById("mobile-menu");e.addEventListener("click",()=>{const n=e.getAttribute("aria-expanded")==="true";e.setAttribute("aria-expanded",!n),t.classList.toggle("hidden")})}function handleScrollToTop(){window.onscroll=function(){e()};function e(){const e=document.getElementById("scroll-to-top");document.body.scrollTop>20||document.documentElement.scrollTop>20?e.style.display="block":e.style.display="none"}}function scrollToTop(){document.body.scrollTop=0,document.documentElement.scrollTop=0}const storedTheme=localStorage.getItem("color-theme");if(storedTheme)document.documentElement.classList.add(storedTheme),handleThemeIcons();else{const e=window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light";localStorage.setItem("color-theme",e),document.documentElement.classList.add(e),handleThemeIcons()}function changeGiscusTheme(e){function t(e){const t=document.querySelector("iframe.giscus-frame");if(!t)return;t.contentWindow.postMessage({giscus:e},"https://giscus.app")}t({setConfig:{theme:e}})}const themeToggleBtn=document.getElementById("theme-toggle");themeToggleBtn.addEventListener("click",()=>{toggleTheme(),handleThemeIcons()}),handleMobileMenu(),handleScrollToTop()